// Purpose: Multi-level logger with atomic level (stub).\n// Example Invocation: ID 116\n\n#include <stdio.h>\n#include <stdatomic.h>\n\nstatic _Atomic int g_log_level = 1;\n#define LOG(lvl,msg) do{ if((lvl)<=atomic_load(&g_log_level)) printf("[L%d] %s\n", (lvl), (msg)); }while(0)\n\nint example_logging_system(void){\n    LOG(1,"Info message"); LOG(2,"Verbose detail (hidden by default)");\n    atomic_store(&g_log_level, 2); LOG(2,"Now visible after level raise");\n    return 0;\n}\n