// Purpose: Security basics â€“ bounds checking, integer overflow notes (stub).\n// Example Invocation: ID 109\n\n#include <stdio.h>\n#include <limits.h>\n\nint example_security_basics(void){\n    printf("[Security Basics]\n");\n    unsigned int a = UINT_MAX; unsigned int b = 10;\n    printf("Potential overflow if adding b to a: a=%u b=%u\n", a, b);\n    if(b > UINT_MAX - a){ printf("Overflow prevented.\n"); }\n    char buf[8]; const char *input="abcdefghijkl";\n    printf("Safe copy with truncation.\n");\n    for(size_t i=0;i<sizeof(buf)-1 && input[i]; i++) buf[i]=input[i]; buf[sizeof(buf)-1]='\0';\n    printf("buf=%s\n", buf);\n    return 0;\n}\n